INSERT INTO `page_text` (`id`, `text`, `nextpageid`, `PlayerConditionID`, `Flags`, `VerifiedBuild`) VALUES (3199, 'Name: Milton$BStatus: Executed$BComment: Caught. Was found without armor or runeblade asleep next to his deathcharger. The deathcharger was returned to Salanar without any serious injury. Death knights are not permitted to sleep.', 3200, 1, 1, 12340);
